sourcesContent:
import { Router } from "express";
import Asignaturas from "../models/Asignaturas";
import Alumnos from "../models/Alumnos";
import { createAsignaturas, deleteAsignaturas, renderAsignaturas, renderEditAsignaturas, statusAsignaturas, updateAsignaturas } from "../controllers/asignaturaController";

const router = Router();

router.get("/", renderAsignaturas);

router.post("/asignaturas/agregar", createAsignaturas);

router.post("/alumnos/agregar", async (req, res) => {
  try {
    const alumnos = Alumnos(req.body);
    await alumnos.save();
    res.redirect("/");
  } catch (error) {
    console.log(error);
  }
});

router.get("/updateAsignatura/:id", renderEditAsignaturas);

router.get("/updateAlumno/:id", async (req, res) => {
  try {
    const alumnos = await Alumnos.findById(req.params.id).lean();
    res.render("editarAlumno", { alumnos });
  } catch (error) {
    console.log(error.message);
  }
});

router.post("/updateAsignatura/:id", updateAsignaturas);

router.post("/updateAlumno/:id", async (req, res) => {
  const { id } = req.params;
  await Alumnos.findByIdAndUpdate(id, req.body);

  res.redirect("/");
});

router.get("/deleteAsignatura/:id", deleteAsignaturas);

router.get("/deleteAlumno/:id", async (req, res) => {
  const { id } = req.params;
  await Alumnos.findByIdAndDelete(id, req.body);

  res.redirect("/");
});

router.get("/statusAsignatura/:id", statusAsignaturas);

export default router;

