sourcesContent:
import { Router } from "express";
import Asignaturas from "../models/Asignaturas";
import Alumnos from "../models/Alumnos";

const router = Router();

router.get("/", (req, res) => {
  res.render("index");
});

router.post("/asignaturas/agregar", async (req, res) => {
  const asignaturas = Asignaturas(req.body);
  await asignaturas.save();
  res.redirect("/");
});

router.post("/alumnos/agregar", async (req, res) => {
  const alumnos = Alumnos(req.body);
  await alumnos.save();
  res.redirect("/");
});

router.get("/update", (req, res) => {
  res.render("editar");
});

export default router;

