import { Schema, model } from "mongoose";

const asignaturaEsquema = new Schema(
  {
    nombre: {
      type: String,
      required: true,
      unique: true,
      trim: true,
    },
    creditos: {
      type: String,
      required: true,
    },
    horas: {
      type: String,
      required: true,
    },
  },
  {
